var p10 = "question1=Which%20of%20the%20following%20organizations%20has%20direct%20authority%20over%20Air%20Force%20logistics%20systems%3F&answer1=c&question1a=Joint%20Chiefs%20of%20Staff%20&question1b=Major%20Commands%20&question1c=DCS%20Logistics,%20Installations,%20and%20Mission%20Support&question1d=Combatant%20Commands%20&q1correct=Correct%21%20DCS%20Logistics,%20Installations,%20and%20Mission%20Support%20is%20the%20only%20organization%20that%20has%20direct%20authority%20over%20Air%20Force%20logistics.&q1incorrect1=Not%20quite.%20That%20group%20does%20not%20have%20direct%20authority%20over%20Air%20Force%20logistics%20systems.%20Please%20try%20again.&q1incorrect2=The%20correct%20answer%20is%20C.%20DCS%20Installation%20and%20Logistics%20is%20the%20only%20organization%20that%20has%20direct%20authority%20over%20Air%20Force%20logistics.%20Please%20review%20the%20content%20on%20page%207%20of%20this%20module.&question2=Complete%20the%20following%20definition%20of%20logistics%3A%20The%20science%20of%20_______%20the%20movement%20and%20maintenance%20of%20forces.&answer2=d&question2a=overseeing&question2b=scheduling%20and%20managing%20&question2c=preparing%20&question2d=planning%20and%20carrying%20out%20&q2correct=Correct%21%20Logistics%20is%20the%20science%20of%20planning%20and%20carrying%20out%20the%20movement%20and%20maintenance%20of%20forces.&q2incorrect1=Not%20quite.%20Please%20try%20again.%20&q2incorrect2=The%20correct%20answer%20is%20D.%20Logistics%20is%20the%20science%20of%20planning%20and%20carrying%20out%20the%20movement%20and%20maintenance%20of%20forces.%20Please%20review%20the%20content%20on%20page%203%20of%20this%20module.&question3=Which%20of%20the%20logistics%20processes%20involves%20transporting%20resources%3F&answer3=b&question3a=Acquisition&question3b=Distribution%20&question3c=Sustainment%20&question3d=Disposition&q3correct=Correct%21%20Distribution%20includes%20all%20activities%20required%20to%20identify%20and%20transfer%20resources%20from%20one%20location%20to%20another.&q3incorrect1=Not%20quite.%20Transporting%20resources%20is%20not%20a%20component%20of%20that%20logistics%20process.%20Please%20try%20again.&q3incorrect2=The%20correct%20answer%20is%20B.%20Distribution%20includes%20all%20activities%20required%20to%20identify%20and%20transfer%20resources%20from%20one%20location%20to%20another.%20Please%20review%20the%20content%20on%20page%2010%20of%20this%20module.";

