var p17 ="question1=%22Dictating%20the%20intensity%20and%20pace%20of%20operations%22%20is%20a%20philosophy%20of%20which%20of%20the%20following%20principles%20of%20war%3F&answer1=c&question1a=Surprise%20&question1b=Objective%20&question1c=Offensive%20&question1d=Security&q1correct=Correct%21%20Act%20rather%20than%20react%20and%20then%20dictate%20the%20time%2C%20place%2C%20purpose%2C%20scope%2C%20intensity%20and%20pace%20of%20operations.&q1incorrect1=Not%20quite.%20This%20principle%20of%20war%20also%20teaches%20you%20to%20act%2C%20rather%20than%20react.%20Please%20try%20again.&q1incorrect2=The%20correct%20answer%20is%20C%29%20Offensive.%20Act%20rather%20than%20react%20and%20then%20dictate%20the%20time%2C%20place%2C%20purpose%2C%20scope%2C%20intensity%20and%20pace%20of%20operations.%20Review%20the%20content%20on%20page%2014%20of%20this%20module.%20&question2=Which%20of%20the%20following%20principles%20of%20logistics%20is%20also%20a%20principle%20of%20war%3F&answer2=b&question2a=Flexibility%20&question2b=Simplicity%20&question2c=Economy%20&question2d=Survivability%20&q2correct=Correct%21%20Simplicity%20is%20both%20a%20principle%20of%20logistics%20and%20a%20principle%20of%20war.&q2incorrect1=Not%20quite.%20This%20principle%20of%20logistics%20is%20important%2C%20but%20it%20is%20not%20a%20principle%20of%20war.%20Please%20try%20again.&q2incorrect2=The%20correct%20answer%20is%20B%29%20Simplicity.%20Review%20the%20content%20on%20pages%2014%20and%2016%20of%20this%20module."

