
//M2_008.htm
var p8 = "question1=Conducting%20an%20Analysis%20of%20Alternatives%20(AoA)%20occurs%20during%20which%20phase%20of%20the%20Acquisition%20Process%3F&answer1=a&question1a=Materiel%20Solution%20Analysis&question1b=Technology%20Development&question1c=Engineering%20and%20Manufacturing%20Development&question1d=Milestone%20B&q1correct=Correct%21%20Conducting%20an%20Analysis%20of%20Alternatives%20(AOA)%20occurs%20during%20Materiel%20Solution%20Analysis.%20&q1incorrect1=Not%20quite.%20Studying%20alternative%20concepts%20should%20occur%20before%20this%20phase.%20Please%20try%20again.&q1incorrect2=The%20correct%20answer%20is%20A.%20Conducting%20an%20Analysis%20of%20Alternatives%20(AoA)%20occurs%20during%20Materiel%20Solution%20Analysis.%20Review%20content%20on%20page%205%20of%20this%20module.&question2=Which%20of%20the%20following%20is/are%20exit%20criteria%20for%20the%20Engineering%20and%20Manufacturing%20phase%20of%20the%20Acquisition%20Process%3F&answer2=d&question2a=Funding%20is%20secured%20&question2b=An%20approved%20Capability%20Development%20Document%20%28CDD%29&question2c=An%20approved%20Technology%20Development%20Strategy%20%28TDS%29&question2d=System%20Demonstrated%20in%20Intended%20Environment&q2correct=Correct%21%20Demonstrating%20the%20system%20in%20its%20intended%20environment%20is%20an%20exit%20criteria%20for%20the%20Engineering%20and%20Manufacturing%20Development%20phase%20of%20the%20Acquisition%20Process.&q2incorrect1=Not%20quite.%20Please%20try%20again.&q2incorrect2=The%20correct%20answer%20is%20D.%20Demonstrating%20the%20system%20in%20its%20intended%20environment%20is%20one%20of%20the%20exit%20criteria%20for%20the%20Engineering%20and%20Manufacturing%20Development%20phase%20of%20the%20Acquisition%20Process.%20Please%20review%20the%20content%20on%20page%207%20of%20this%20module.&question3=In%20the%20Materiel%20Solution%20Analysis%20phase%20of%20the%20Acquisition%20Process%2C%20an%20approved%20ICD%20exists%20as%3A&answer3=a&question3a=Entrance%20Criteria&question3b=An%20activity&question3c=Exit%20Criteria&question3d=None%20of%20the%20above%20-%20it%20occurs%20after%20the%20Materiel%20Solution%20Analysis%20phase&q3correct=Correct%21%20The%20Materiel%20Solution%20Analysis%20phase%20cannot%20begin%20without%20an%20approved%20ICD.&q3incorrect1=Not%20quite.%20An%20approved%20ICD%20occurs%20before%20your%20choice.%20Please%20try%20again.&q3incorrect2=The%20correct%20answer%20is%20A.%20The%20Materiel%20Solution%20Analysis%20phase%20cannot%20begin%20without%20an%20approved%20ICD.%20Review%20the%20content%20on%20page%205%20of%20this%20module.";

//M2_019.htm
var p19 ="question1=The%20majority%20of%20the%20cost%20of%20a%20weapons%20system%20occurs%20during%20which%20phase%20of%20the%20lifecycle%3F&answer1=c&question1a=Design%20&question1b=Manufacture%20&question1c=Support%20&question1d=Disposal&q1correct=Correct%21%20The%20majority%20of%20the%20cost%20occurs%20during%20operations%20and%20for%20maintenance%20in%20the%20support%20phase.%20Money%20spent%20during%20the%20design%20phase%20is%20overshadowed%20by%20the%20costs%20during%20the%20remainder%20of%20the%20lifecycle.&q1incorrect1=Not%20quite.%20Although%20there%20can%20be%20significant%20costs%20during%20that%20phase%2C%20the%20majority%20of%20the%20costs%20occur%20at%20a%20different%20time.%20Please%20try%20again.&q1incorrect2=The%20correct%20answer%20is%20C.%20The%20majority%20of%20the%20cost%20occurs%20during%20operations%20and%20maintenance%20in%20the%20support%20phase.%20Money%20spent%20during%20the%20design%20phase%20is%20overshadowed%20by%20the%20costs%20during%20the%20remainder%20of%20the%20lifecycle.%20Review%20content%20on%20page%2014%20of%20this%20module.&question2=A%20design%20change%20is%20expected%20to%20cost%20the%20project%20One%20Million%20dollars%20during%20the%20design%20phase.%20If%20this%20change%20is%20discovered%20after%20the%20system%20has%20been%20deployed%2C%20how%20much%20will%20it%20cost%20to%20make%20the%20change%20then%3F&answer2=d&question2a=%241%2C000%20&question2b=%241%2C000%2C000%20&question2c=%2410%2C000%2C000%20&question2d=%241%2C000%2C000%2C000&q2correct=Correct%21%20If%20the%20change%20is%20required%20after%20the%20system%20is%20deployed%2C%20it%20will%20cost%20approximately%20One%20BILLION%20dollars%20to%20complete.&q2incorrect1=Not%20quite.%20A%20change%20after%20deployment%20will%20cost%20much%20more%20than%20that.%20Please%20try%20again.&q2incorrect2=The%20correct%20answer%20is%20D%20-%20one%20billion%20dollars.%20Although%20one%20million%20dollars%20may%20seem%20like%20too%20much%20money%20to%20spend%20for%20a%20change%20in%20the%20design%20phase%2C%20discovering%20that%20the%20change%20must%20be%20made%20after%20the%20system%20is%20deployed%20will%20cost%20one%20billion%20dollars.%20Review%20the%20content%20on%20page%2016%20of%20this%20module."

//M2_025.htm
var p25 ="question1=Which%20of%20these%20types%20of%20contracts%20has%20the%20least%20amount%20of%20risk%20for%20the%20government%3F%20&answer1=d&question1a=Cost%20Sharing%20&question1b=Cost%20Plus%20Incentive%20Fee%20&question1c=Cost%20Plus%20Fixed%20Fee%20&question1d=Firm-Fixed%20Price&q1correct=Correct%21%20The%20contractor%20assumes%20the%20majority%20of%20the%20risk%20in%20a%20Firm-Fixed%20Price%20contract.&q1incorrect1=Not%20quite.%20The%20government%20can%20assume%20less%20risk%20with%20a%20different%20type%20of%20contract.%20Please%20try%20again.&q1incorrect2=The%20correct%20answer%20is%20D%29.%20The%20government%20assumes%20the%20least%20amount%20of%20risk%20in%20a%20Firm-Fixed%20Price%20contract.%20Review%20content%20on%20page%2021%20of%20this%20module.%20&question2=Which%20of%20the%20following%20correctly%20orders%20the%20contracting%20flow%20of%20authority%20from%20top%20to%20bottom%3F&answer2=b&question2a=President%20%3E%20Contr.%20Officer%20%3E%20Sec.%20of%20Def.%20%3E%20Sec.%20of%20AF%20%3E%20MAJCOM%20Head%20of%20Contr.%20Authority%20&question2b=President%20%3E%20Sec.%20of%20Def.%20%3E%20Sec.%20of%20AF%20%3E%20MAJCOM%20Head%20of%20Contr.%20Authority%20%3E%20Contr.%20Officer&question2c=Contr.%20Officer%20%3E%20Sec.%20of%20Def.%20%3E%20Sec.%20of%20AF%20%3E%20MAJCOM%20Head%20of%20Contr.%20Authority%20%3E%20President&question2d=Contr.%20Officer%20%3E%20President%20%3E%20MAJCOM%20Head%20of%20Contr.%20Authority%20Sec.%20of%20Def.%20%3E%20Sec.%20of%20AF&q2correct=Correct%21%20The%20President%20is%20at%20the%20top%20of%20the%20flow%2C%20while%20the%20Contracting%20Officer%20is%20at%20the%20bottom.&q2incorrect1=Not%20quite.%20The%20Contracting%20Officer%20is%20not%20ordered%20correctly.%20Please%20try%20again.&q2incorrect2=The%20correct%20answer%20is%20B%29.%20The%20President%20is%20at%20the%20top%20of%20the%20flow%2C%20followed%20by%20the%20Secretary%20of%20Defense%20and%20Secretary%20of%20the%20AirForce.%20The%20Contracting%20Officer%20is%20at%20the%20bottom.%20Review%20the%20content%20on%20page%2022%20of%20this%20module.&question3=Which%20of%20the%20following%20types%20of%20contract%20officers%20is%20responsible%20for%20modifying%20contracts%3F&answer3=b&question3a=Procuring%20&question3b=Administrative%20&question3c=Termination%20&question3d=Any%20of%20the%20above&q3correct=Correct%21%20The%20Administrative%20Contract%20Officer%20is%20responsible%20for%20administering%20and%20modifying%20contracts.&q3incorrect1=Not%20quite.%20That%20Contracting%20Officer%20is%20not%20responsible%20for%20modifying%20contracts.%20Please%20try%20again.&q3incorrect2=The%20correct%20answer%20is%20B%29.The%20Administrative%20Contract%20Officer%20is%20responsible%20for%20administering%20and%20modifying%20contracts.%20Review%20the%20content%20on%20page%2022%20of%20this%20module."

//M2_030.htm
var p30 ="question1=Which%20of%20the%20integrated%20logistics%20support%20elements%20is%20one%20of%20the%20most%20significant%20determinants%20of%20life%20cycle%20costs%3F&answer1=a&question1a=Maintenance%20Planning&question1b=Supply%20Support%20&question1c=Technical%20Data&question1d=Design%20Interface&q1correct=Correct%21%20Maintenance%20Planning%20is%20the%20process%20conducted%20to%20evolve%20and%20establish%20maintenance%20concepts%20and%20requirements%20for%20the%20lifetime%20of%20the%20system.%20It%20is%20obviously%20one%20of%20the%20most%20significant%20determinants%20of%20life%20cycle%20costs.&q1incorrect1=Not%20quite.%20Although%20that%20element%20does%20have%20costs%2C%20the%20costs%20do%20not%20significantly%20affect%20the%20life%20cycle%20costs.%20Please%20try%20again.&q1incorrect2=The%20correct%20answer%20is%20A%29.%20Maintenance%20Planning%20is%20the%20process%20conducted%20to%20evolve%20and%20establish%20maintenance%20concepts%20and%20requirements%20for%20the%20lifetime%20of%20the%20system.%20It%20is%20obviously%20one%20of%20the%20most%20significant%20determinants%20of%20life%20cycle%20costs.%20Review%20content%20on%20page%2027%20of%20this%20module.&question2=Hypothetically%20speaking%2C%20building%20a%20main%20battle%20tank%20which%20is%201%20inch%20too%20wide%20to%20fit%20into%20the%20back%20of%20the%20aircraft%20which%20was%20intended%20to%20be%20used%20to%20move%20it%20would%20be%20a%20failure%20by%20which%20element%20of%20logistics%3F&answer2=d&question2a=Supply%20Support%20&question2b=Support%20Equipment&question2c=Facilities%20&question2d=Packaging%2C%20Handling%2C%20Storage%20and%20Transportation&q2correct=Correct%21%20Packaging%2C%20Handling%2C%20Storage%20and%20Transportation%20deals%20with%20the%20requirements.%20In%20this%20scenario%2C%20the%20requirements%20for%20transporting%20the%20battle%20tank%20were%20not%20properly%20researched.&q2incorrect1=Not%20quite.%20Please%20try%20again.&q2incorrect2=The%20correct%20answer%20is%20D%29.%20In%20this%20scenario%2C%20the%20requirements%20for%20transporting%20the%20battle%20tank%20were%20not%20properly%20researched.%20Packaging%2C%20Handling%2C%20Storage%20and%20Transportation%20deals%20with%20the%20requirements.%20Review%20the%20content%20on%20page%2029%20of%20this%20module."


