
//M3_009.htm
var p9 = "question1=Which%20of%20the%20following%20activities%20fall%20under%20the%20definition%20of%20distribution%3F&answer1=d&question1a=Transportation%20&question1b=Warehousing%20and%20Inventory%20control&question1c=Site%20and%20location%20analysis%20&question1d=All%20of%20the%20above&q1correct=Correct%21%20All%20of%20these%20activities%20are%20included%20in%20the%20definition%20of%20distribution.&q1incorrect1=Not%20quite.%20Please%20try%20again.&q1incorrect2=The%20correct%20answer%20is%20D%29.%20All%20of%20these%20activities%20are%20included%20in%20the%20definition%20of%20distribution.%20Review%20content%20on%20page%203%20of%20this%20module.&question2=Which%20of%20the%20following%20is%20not%20one%20of%20the%20five%20modes%20of%20transportation%3F&answer2=a&question2a=Space%20&question2b=Pipeline%20&question2c=Water%20&question2d=Motor&q2correct=Correct%21%20Space%20is%20not%20a%20mode%20of%20transportation.%20The%20five%20modes%20of%20transportation%20are%20air%2C%20water%2C%20motor%2C%20rail%20and%20pipeline.&q2incorrect1=Not%20quite.%20That%20is%20one%20of%20the%20modes%20of%20transportation.%20Please%20try%20again.&q2incorrect2=The%20correct%20answer%20is%20A%29.%20The%20five%20modes%20of%20transportation%20are%20air%2C%20water%2C%20motor%2C%20rail%20and%20pipeline.%20Review%20the%20content%20on%20page%205%20of%20this%20module.";


//M3_014.htm
var p14 ="question1=The%20military%20adopted%20Time%20Definite%20Delivery%20instead%20of%20Just-in-Time%20Delivery%20because&answer1=b&question1a=JIT%20had%20too%20large%20an%20inventory%20&question1b=JIT%20is%20too%20risky&question1c=JIT%20was%20not%20proven%20to%20work%20in%20industry%20&question1d=All%20of%20the%20above&q1correct=Correct%21%20The%20military%27s%20needs%20are%20different%20than%20industry%20and%20operations%20cannot%20afford%20the%20risks%20of%20items%20not%20arriving%20on%20time.&q1incorrect1=Not%20quite.%20Please%20try%20again.&q1incorrect2=The%20correct%20answer%20is%20B.%20JIT%20has%20been%20proven%20to%20be%20both%20economical%20and%20feasible%20in%20industry.%20Delivering%20ammo%2C%20parts%20and%20medical%20supplies%20just%20in%20the%20nick%20of%20time%20before%20the%20enemy%20descends%20upon%20you%20is%20not%20a%20good%20plan%20of%20action.%20So%20instead%20of%20just-in-time%20delivery%2C%20you%20want%20a%20predictable%2C%20reliable%20and%20safe%20time%20of%20arrival%2C%20to%20reduce%20the%20uncertainty%20and%20to%20support%20the%20operation.%20In%20time%2C%20our%20particular%20spin%20on%20JIT%20evolved%20into%20what%20we%20now%20know%20as%20Time%20Definite%20Delivery%20%28TDD%29.%20Review%20content%20on%20page%2013%20of%20this%20module.%20&question2=Which%20of%20the%20following%20is%20not%20one%20of%20the%20key%20concepts%20of%20agile%20logistics%3F&answer2=c&question2a=Total%20Asset%20Visibility%20&question2b=Right-sized%20inventories%20&question2c=Deregulation&question2d=Cycle%20time%20reduction&q2correct=Correct%21%20Deregulation%20required%20that%20companies%20reduce%20inventories%20and%20improve%20distribution%20to%20remain%20competitive.&q2incorrect1=Not%20quite.%20That%20is%20one%20of%20the%20key%20concepts%20of%20agile%20logistics.%20Please%20try%20again.&q2incorrect2=The%20correct%20answer%20is%20C.%20Deregulation%20%28by%20reducing%20transportation%20times%20and%20costs%29%20enabled%20companies%20to%20reduce%20inventory%20and%20remain%20competitive.%20The%20four%20key%20concepts%20of%20agile%20logistics%20are%20Cycle%20time%20reduction%2C%20Right-sized%20inventories%2C%20Total%20Asset%20Visability%20and%20Time%20Definite%20Delivery.%20Review%20the%20content%20on%20page%2013%20of%20this%20module.&question3=Which%20of%20the%20following%20key%20concepts%20of%20agile%20logistics%20enables%20you%20to%20accomplish%20contingency%20planning%20and%20improve%20reaction%3F&answer3=c&question3a=Cycle%20time%20reduction%20&question3b=Right-sized%20inventories&question3c=Total%20Asset%20Visibility%20&question3d=Time%20Definite%20Delivery&q3correct=Correct%21%20Total%20Asset%20Visibility%20gives%20you%20confidence%20and%20allows%20you%20to%20plan%20accordingly%20if%20necessary.&q3incorrect1=Not%20quite.%20This%20same%20key%20concept%20also%20helps%20build%20confidence%20in%20the%20distribution%20process.%20Please%20try%20again.&q3incorrect2=The%20correct%20answer%20is%20C%29.%20Total%20asset%20visibility%20%28TAV%29%20enables%20you%20to%20accomplish%20contingency%20planning%20and%20improve%20reaction.%20Using%20a%20National%20Stock%20Number%20and%20following%20the%20Federal%20Catalog%20System%20Policy%20Manual%2C%20the%20military%20is%20better%20equipped%20to%20use%20a%20web-based%20tool%2C%20such%20as%20the%20Global%20Transportation%20Network%20%28GTN%29%20to%20track%20assets.%20Total%20asset%20visibility%20gives%20you%20confidence%20and%20allows%20you%20to%20plan%20accordingly%20if%20necessary.%20Review%20the%20content%20on%20page%2013%20of%20this%20module."

//M3_020.htm
var p20 ="question1=Which%20of%20the%20following%20is%20not%20one%20of%20the%20USTRANSCOM%20Component%20Commands%3F&answer1=c&question1a=AMC&question1b=MSC%20&question1c=CRAF%20&question1d=SDDC&q1correct=Correct%21%20CRAF%20supports%20AMC%20and%20is%20not%20a%20component%20command.&q1incorrect1=Not%20quite.%20That%20is%20one%20of%20the%20USTRANSCOM%20Component%20Commands.%20Please%20try%20again.&q1incorrect2=The%20correct%20answer%20is%20C%29.%20Three%20component%20commands%20are%20the%20Air%20Force%27s%20Air%20Mobility%20Command%20%28AMC%29%2C%20the%20Army%27s%20Military%20Surface%20Deployment%20and%20Distribution%20Command%20%28SDDC%29%2C%20and%20the%20Navy%27s%20Military%20Sealift%20Command%20%28MSC%29.%20Review%20the%20content%20on%20page%2017%20of%20this%20module.&question2=CORE%20supports%20which%20of%20the%20USTRANSCOM%20Component%20Commands%3F&answer2=d&question2a=AMC%20&question2b=MSC%20&question2c=VISA%20&question2d=SDDC&q2correct=Correct%21%20CORE%20supports%20SDDC.&q2incorrect1=Not%20quite.%20Hint%3A%20CORE%20is%20the%20Contingency%20Response%20Program.%20Please%20try%20again.&q2incorrect2=The%20correct%20answer%20is%20D%29.%20The%20Contingency%20Response%20Program%20%28CORE%29%20supports%20the%20acquisition%20of%20domestic%20civil%20transportation%20resources%20during%20military%20deployments%20under%20the%20command%20of%20the%20SDDC.%20Review%20the%20content%20on%20page%2017%20of%20this%20module."